    E_ATA(0,0) = Cdwi[1][1] + Cdwi[2][2] + Cwi[0][0]*Cwi[1][1] + Cwi[0][0]*Cwi[2][2] + Cwi[0][0]*Ewi[1]*Ewi[1] + Cwi[0][0]*Ewi[2]*Ewi[2] + 2*Cwi[0][1]*Cwi[0][1] + 2*Cwi[0][1]*Edwi[2] + 4*Cwi[0][1]*Ewi[0]*Ewi[1] + 2*Cwi[0][2]*Cwi[0][2] - 2*Cwi[0][2]*Edwi[1] + 4*Cwi[0][2]*Ewi[0]*Ewi[2] + 3*Cwi[1][1]*Cwi[1][1] + 2*Cwi[1][1]*Cwi[2][2] + Cwi[1][1]*Ewi[0]*Ewi[0] + 6*Cwi[1][1]*Ewi[1]*Ewi[1] + 2*Cwi[1][1]*Ewi[2]*Ewi[2] + 4*Cwi[1][2]*Cwi[1][2] + 8*Cwi[1][2]*Ewi[1]*Ewi[2] + 3*Cwi[2][2]*Cwi[2][2] + Cwi[2][2]*Ewi[0]*Ewi[0] + 2*Cwi[2][2]*Ewi[1]*Ewi[1] + 6*Cwi[2][2]*Ewi[2]*Ewi[2] + Edwi[1]*Edwi[1] - 2*Edwi[1]*Ewi[0]*Ewi[2] + Edwi[2]*Edwi[2] + 2*Edwi[2]*Ewi[0]*Ewi[1] + Ewi[0]*Ewi[0]*Ewi[1]*Ewi[1] + Ewi[0]*Ewi[0]*Ewi[2]*Ewi[2] + Ewi[1]*Ewi[1]*Ewi[1]*Ewi[1] + 2*Ewi[1]*Ewi[1]*Ewi[2]*Ewi[2] + Ewi[2]*Ewi[2]*Ewi[2]*Ewi[2];
    E_ATA(0,1) = -Cdwi[0][1] - 3*Cwi[0][0]*Cwi[0][1] - Cwi[0][0]*Edwi[2] - 3*Cwi[0][0]*Ewi[0]*Ewi[1] - 3*Cwi[0][1]*Cwi[1][1] - Cwi[0][1]*Cwi[2][2] - 3*Cwi[0][1]*Ewi[0]*Ewi[0] - 3*Cwi[0][1]*Ewi[1]*Ewi[1] - Cwi[0][1]*Ewi[2]*Ewi[2] - 2*Cwi[0][2]*Cwi[1][2] + Cwi[0][2]*Edwi[0] - 2*Cwi[0][2]*Ewi[1]*Ewi[2] + Cwi[1][1]*Edwi[2] - 3*Cwi[1][1]*Ewi[0]*Ewi[1] - Cwi[1][2]*Edwi[1] - 2*Cwi[1][2]*Ewi[0]*Ewi[2] - Cwi[2][2]*Ewi[0]*Ewi[1] - Edwi[0]*Edwi[1] + Edwi[0]*Ewi[0]*Ewi[2] - Edwi[1]*Ewi[1]*Ewi[2] - Edwi[2]*Ewi[0]*Ewi[0] + Edwi[2]*Ewi[1]*Ewi[1] - Ewi[0]*Ewi[0]*Ewi[0]*Ewi[1] - Ewi[0]*Ewi[1]*Ewi[1]*Ewi[1] - Ewi[0]*Ewi[1]*Ewi[2]*Ewi[2];
    E_ATA(0,2) = -Cdwi[0][2] - 3*Cwi[0][0]*Cwi[0][2] + Cwi[0][0]*Edwi[1] - 3*Cwi[0][0]*Ewi[0]*Ewi[2] - 2*Cwi[0][1]*Cwi[1][2] - Cwi[0][1]*Edwi[0] - 2*Cwi[0][1]*Ewi[1]*Ewi[2] - Cwi[0][2]*Cwi[1][1] - 3*Cwi[0][2]*Cwi[2][2] - 3*Cwi[0][2]*Ewi[0]*Ewi[0] - Cwi[0][2]*Ewi[1]*Ewi[1] - 3*Cwi[0][2]*Ewi[2]*Ewi[2] - Cwi[1][1]*Ewi[0]*Ewi[2] + Cwi[1][2]*Edwi[2] - 2*Cwi[1][2]*Ewi[0]*Ewi[1] - Cwi[2][2]*Edwi[1] - 3*Cwi[2][2]*Ewi[0]*Ewi[2] - Edwi[0]*Edwi[2] - Edwi[0]*Ewi[0]*Ewi[1] + Edwi[1]*Ewi[0]*Ewi[0] - Edwi[1]*Ewi[2]*Ewi[2] + Edwi[2]*Ewi[1]*Ewi[2] - Ewi[0]*Ewi[0]*Ewi[0]*Ewi[2] - Ewi[0]*Ewi[1]*Ewi[1]*Ewi[2] - Ewi[0]*Ewi[2]*Ewi[2]*Ewi[2];
    E_ATA(1,1) = Cdwi[0][0] + Cdwi[2][2] + 3*Cwi[0][0]*Cwi[0][0] + Cwi[0][0]*Cwi[1][1] + 2*Cwi[0][0]*Cwi[2][2] + 6*Cwi[0][0]*Ewi[0]*Ewi[0] + Cwi[0][0]*Ewi[1]*Ewi[1] + 2*Cwi[0][0]*Ewi[2]*Ewi[2] + 2*Cwi[0][1]*Cwi[0][1] - 2*Cwi[0][1]*Edwi[2] + 4*Cwi[0][1]*Ewi[0]*Ewi[1] + 4*Cwi[0][2]*Cwi[0][2] + 8*Cwi[0][2]*Ewi[0]*Ewi[2] + Cwi[1][1]*Cwi[2][2] + Cwi[1][1]*Ewi[0]*Ewi[0] + Cwi[1][1]*Ewi[2]*Ewi[2] + 2*Cwi[1][2]*Cwi[1][2] + 2*Cwi[1][2]*Edwi[0] + 4*Cwi[1][2]*Ewi[1]*Ewi[2] + 3*Cwi[2][2]*Cwi[2][2] + 2*Cwi[2][2]*Ewi[0]*Ewi[0] + Cwi[2][2]*Ewi[1]*Ewi[1] + 6*Cwi[2][2]*Ewi[2]*Ewi[2] + Edwi[0]*Edwi[0] + 2*Edwi[0]*Ewi[1]*Ewi[2] + Edwi[2]*Edwi[2] - 2*Edwi[2]*Ewi[0]*Ewi[1] + Ewi[0]*Ewi[0]*Ewi[0]*Ewi[0] + Ewi[0]*Ewi[0]*Ewi[1]*Ewi[1] + 2*Ewi[0]*Ewi[0]*Ewi[2]*Ewi[2] + Ewi[1]*Ewi[1]*Ewi[2]*Ewi[2] + Ewi[2]*Ewi[2]*Ewi[2]*Ewi[2];
    E_ATA(1,2) = -Cdwi[1][2] - Cwi[0][0]*Cwi[1][2] - Cwi[0][0]*Ewi[1]*Ewi[2] - 2*Cwi[0][1]*Cwi[0][2] + Cwi[0][1]*Edwi[1] - 2*Cwi[0][1]*Ewi[0]*Ewi[2] - Cwi[0][2]*Edwi[2] - 2*Cwi[0][2]*Ewi[0]*Ewi[1] - 3*Cwi[1][1]*Cwi[1][2] - Cwi[1][1]*Edwi[0] - 3*Cwi[1][1]*Ewi[1]*Ewi[2] - 3*Cwi[1][2]*Cwi[2][2] - Cwi[1][2]*Ewi[0]*Ewi[0] - 3*Cwi[1][2]*Ewi[1]*Ewi[1] - 3*Cwi[1][2]*Ewi[2]*Ewi[2] + Cwi[2][2]*Edwi[0] - 3*Cwi[2][2]*Ewi[1]*Ewi[2] - Edwi[0]*Ewi[1]*Ewi[1] + Edwi[0]*Ewi[2]*Ewi[2] - Edwi[1]*Edwi[2] + Edwi[1]*Ewi[0]*Ewi[1] - Edwi[2]*Ewi[0]*Ewi[2] - Ewi[0]*Ewi[0]*Ewi[1]*Ewi[2] - Ewi[1]*Ewi[1]*Ewi[1]*Ewi[2] - Ewi[1]*Ewi[2]*Ewi[2]*Ewi[2];
